function HandleJSON(data, status)
{
	if (status !== "success") {
		return false;
	}
	else if (data.status === "error" && data.message.length > 0) {
		alert(data.message);
	}
	else if (data.status === "error" && data.refresh === true && data.url) {
		window.location.href = data.url;
		return false;
	}

	if (data.node) {
		if (data.node.inside === true) {
			$(data.node.where).html(data.node.content);
		} else {
			$(data.node.where).replaceWith(data.node.content);
		}

		$(data.node.hide).css("display", "none");
		$(data.node.remove).remove();
	}

	if (data.nodes) {
		for (i in data.nodes) {
			if (data.nodes[i].inside === true) {
				$(data.nodes[i].where).html(data.nodes[i].content);
			} else {
				$(data.nodes[i].where).replaceWith(data.nodes[i].content);
			}

			$(data.nodes[i].hide).css("display", "none");
			$(data.nodes[i].remove).remove();
		}
	}

	if (data.input) {
		var element = $(data.input.element);
		switch($(element).attr("type")){
		case "checkbox":
			if (data.input.value == true) {
				$(element).attr("checked", true);
				break;
			}
			$(element).attr("checked", false);
			break;
		default:
			$(element).val(data.input.value);
		}
	}

	return true;
}


