function CustomDateRange(selectedDate)
{
	var option = $(this).hasClass("date-from") == true ? "minDate" : "maxDate",
		instance = $(this).data("datepicker"),
		date = $.datepicker.parseDate(instance.settings.dateFormat || $.datepicker._defaults.dateFormat, selectedDate, instance.settings );

	if (option == "minDate") {
		$(this).parent().find("input.date-to").datepicker("option", option, date);
		return;
	}

	$(this).parent().find("input.date-from").datepicker("option", option, date);
}


(function($) {
	$.fn.SlideShow = function(opts) {
		var e = null;
		var timer = null;
		var count = 0;
		var current = 0;
		var next = 0;

		var options = $.extend({
			"delay": "5000",
			"slides": ".slides",
			"fading": "1500",
			"nav": ""
		}, opts || {});

		var slide = function() {
			if (next == current) {
				next++;
				if (next >= count) next = 0;
				return;
			}

			if ($(e).hasClass("slide")) { return; }

			$(e).addClass("slide");

			$(e).find(options.nav + " a").removeClass("active");
			$(e).find(options.nav + " a").eq(next).addClass("active");

			$(e).find(options.slides + " li").css({"z-index": 1, "display": "none", "opacity": 0});
			$(e).find(options.slides + " li").eq(next).css({"z-index": 1, "display": "block", "opacity": 1});
			$(e).find(options.slides + " li").eq(current)
				.stop(true, true)
				.css({"z-index": 2, "display": "block", "opacity": 1})
				.fadeTo(options.fading, 0, function(){
					$(this).css("display", "none");
					$(e).removeClass("slide");
				});

			current = next;

			next++;
			if (next >= count) next = 0;
		}

		return this.each(function(i,v) {
			e = $(v);
			count = $(e).find(options.slides + " li").length;
			timer = window.setInterval(function (){slide()}, options.delay);

			// first slide
			$(e).find(options.slides + " li").css({"z-index": 1, "display": "none"});
			$(e).find(options.slides + " li:first").css({"display": "block", "z-index": 2});

			if (options.nav.length > 0) {
				var bullets = $("<ul>").attr("class", options.nav.replace(/[^a-z\-\d]+/i, ""));
				for (var i = 0; i < count; i++) {
					$(bullets).append('<li><a href="#"></a></li>');
				}

				$(bullets).appendTo(e);

				$(e).find(options.nav + " a:first").addClass("active");

				$(e).find(options.nav + " a").live("click", function() {
					window.clearInterval(timer);
					next = $(this).parent().index();
					slide();
					timer = window.setInterval(function (){slide()}, options.delay);
					return false;
				});
			}
			slide();
		});
	}
})(jQuery);
